.header {
  border-bottom: 2px solid #ECF3FF;
  margin: 0 0 60px 0;
  &__top {
    height: 40px;
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .header-top-menu {
    display: flex;
    &__block {
      margin: 0 30px 0 0;
    }
    &__link {
      color: #5D6C7B;
      text-decoration: none;
      font-size: 12px;
      line-height: 17px;        
    }
  }
  .block-social img{
    margin: 0 8px 0 0;
  }
  .header-contact {
    display: flex;
    flex: 0 0 150px;

    &__phone {
      font-size: 12px;
      line-height: 22px;
      font-weight: bold;
      color: #282739;
    }
    &__phone-icon {
      margin: 0 0 0 8px;
    }
  }

  &__middle {
    margin: 20px 0 35px 0;
    .wrapper {
      display: flex;
      align-items: center;
      justify-content: space-between;
    }
    .header-logo {
      width: 165px;
    }
    .header-catalog {
      margin: 0 20px 0 0;
    }
    .menu-catalog {
      padding: 12px 15px;
      display: flex;
      justify-content: center;
      align-items: center;
      background-color: $main-blue-color;
      color: #fff;
      border-radius: 5px;
      font-size: 16px;
      font-weight: 600;
      &__icon {
        margin: 0 15px 0 0;
      }
    }
    .header-search {
      height: 40px;
      border: 1.5px solid $main-blue-color;
      border-radius: 5px;
    }
    .header-search {
      max-width: 605px;
      .wrapper {
        display: flex;
        align-items: center;
      }
    }
    .form-search {
      display: flex;
      width: 100%;
    }
    .search-dropdown {
      width: 76px;
      height: 36px;
      display: flex;
      margin: 2px 0 0 2px;
      justify-content: center;
      align-items: center;
      border-radius: 5px;
      background-color: $bg-color;
      &__label {
        margin: 0 10px 0 0;
        font-size: 14px;
        color: #282739;
      }
      &__icon {
        background-image: url('./images/header/arrow-down.svg');
        background-repeat: no-repeat;
        width: 8px;
        height: 7px;            
      }
    }
    .searchbar {
      position: relative;
      max-width: 100%;
      
      &__input {
        width: 485px;
        height: 36px;
        border: 0;
        outline: none;
        padding-left: 10px;
      }
      &__input::placeholder {
        font-size: 14px;
        color: #5D6C7B;
      }
      &__submit {
        position: absolute;
        top: 0;
        right: 0;
        width: 40px;
        height: 41px;
        border: 0;
        background-color: $main-blue-color;
        background-image: url('./images/header/search-icon.svg');
        background-repeat: no-repeat;
        background-position: center;
      }
    }
    .header-shopping {
      width: 175px;
      display: flex;
      margin: 0 0 0 47px;
      align-items: baseline;
      &__block {
        margin: 0 5px 0 0;
      }
      &__block img {
        padding: 0 10px 0 0;
      }
      .cart {
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 14px;
        line-height: 26px;
      }
    }
  }
  
  &__menu {
    height: 40px;
    background-color: $bg-color;
  }
  .header-menu {
    &__menu {
      display: flex;
      align-items: center;
      height: 40px;
    }
  }
  .menu-list {
    list-style: none;
    display: flex;
    padding: 0;
    li {
      margin: 0 30px 0 0;
    }
    &__link {
      text-decoration: none;
      font-weight: 700;
      font-size: 14px;
      line-height: 20px;
      color: #282739;
    }
    &__menu-icon {
      background-color: $main-blue-color;
      color: #fff;
      font-size: 10px;
      padding: 5px 12px;
      border-radius: 300px;
      &.bank {
        margin: 0 0 0 8px;
      }
      &.percent {
        background-image: url('./images/header/percentage.svg');
        background-repeat: no-repeat;
        background-position: center;
        margin: 0 0 0 8px;
      }
    }
  }
}

sources.src\\products.scss:
.sort-btn {
  border: 1.3px solid $main-blue-color;
  color: $main-blue-color;
  border-radius: 5px;
  display: flex;
  font-size: 12px;
  align-items: center;
}
.product {
  margin-bottom: 300px;
  &__page-content {
    display: flex;
  }
  /* Sort by Section */
  &__sortby-header {
    display: flex;
    justify-content: space-between;
    margin: 0 0 40px 0;
    .label {
      font-size: 25px;
      color: $main-black-color;
      text-transform: uppercase;
      font-weight: 700;
    }
  }
  .sortby-controls {
    display: flex;
    align-items: center;
    &__label {
      color: $main-black-color;
      font-size: 14px;
      font-weight: 600;
      margin-right: 20px;
    }
    &__price {
      padding: 10px 10px;
      margin-right: 20px;
    }
    &__params {
      padding: 10px 15px;
    }
    img {
     margin-left: 15px; 
    }
  }

  // Sidebar filter Section
  .category-filters {
    max-width: 255px;
    flex: 0 0 100%;
    background-color: $bg-color;
    margin: 0 40px 0 0;
    height: 590px;
    &__container {
      max-width: 212px;
      margin: 0 auto;
      padding: 20px 0;
    }
  }
  .filter-block {
    margin: 0 0 20px 0;
    &__header {
      margin-bottom: 20px;
      font-weight: 600;
    }
    &__item {
      margin-bottom: 10px;
      display: flex;
      align-items: center;
      color: $main-black-color;
      font-size: 14px;
      .filter {
        margin-right: 10px;
      }
    }
    &__item:last-child {
      margin: 0;
    }
    .filter-checkbox {
      position: relative;
      padding: 0 0 0 20px;
      line-height: 18px;
      
      input:checked + .filter-checkbox__hidden {
        position: absolute;
        top: 0;
        left: 0;
        width: 16px;
        height: 16px;
        background-color: #fff;
        border: 0;
        border-radius: 2px;
        background-image: url('./images/filters/checked.svg');
        background-repeat: no-repeat;
        background-position: center;
      }
      &__input {
        appearance: none;
        -moz-appearance: none;
        -webkit-appearance: none;
      }
      &__hidden {
        top: 0;
        left: 0;
        position: absolute;
        width: 16px;
        height: 16px;
        background-color: #fff;
        border: 1px solid #5D6C7B;
        border-radius: 2px;
      }
    }
  }
  .show-more-filters {
    font-size: 14px;
    color: $main-blue-color;
    text-align: center;
  }
  .price-range {
    margin: 0 0 17px 0;
  }
  .price-input {
    display: flex;
    justify-content: space-between;
    align-items: center;
    &__from {
      width: 68px;
      height: 35px;
      border: 1px solid #EAEBED;
      border-radius: 5px;
      padding-left: 10px;
      outline: none;
      border: 0;
    }
    &__to {
      width: 68px;
      height: 35px;
      border: 1px solid #EAEBED;
      border-radius: 5px;
      padding-left: 10px;
      outline: none;
      border: 0;
    }    
  }

  /* Product Body section */
  .products-body {
    max-width: 825px;
    flex: 0 0 825px;
  }
  .products-list {
    display: flex;
    justify-content: flex-start;
    margin-bottom: 30px;
  }
  .product-list-item {
    border: 1.5px solid #EAEBED;
    border-radius: 10px;
    max-width: 252px;
    margin: 0 20px 0 0;
    flex: 0 0 100%;
  }
  .product-thumbnail {
    background-color: #F0F1F5;
    &__container {
      padding: 10px;
    }
    img {
      width: 200px;
    }
  }
  .product-marketing {
    display: flex;
    justify-content: space-between;

    &__label {
      font-size: 12px;
      padding: 6px 10px;
      border-radius: 5px;
      height: 15px;
      line-height: 16px;
    }
    &__label.hit {
      background-color: #EE685F;
      text-transform: uppercase;
      color: #fff;
    }
    &__label.new {
      background-color: #75D14A;
      color: #fff;
    }
    &__compare {
      background-color: #fff;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: 50px;
      width: 35px;
      height: 35px;
    }
    &__compare img {
      width: 20px;
      height: 12px;
    
    }
  }
  .product-body {
    &__container {
      padding: 20px 25px;
    }
    .product-title {
      font-weight: 700;
      margin: 0 0 20px 0;
    }
    .params-list {
      display: flex;
      justify-content: space-between;
      margin-bottom: 10px;
      align-items: center;
      line-height: 28px;
      &__item {
        color: #5D6C7B;
        font-size: 14px;
      }
    }
    .product-shopping {
      display: flex;
      justify-content: space-between;
      margin: 0 0 20px 0;

      .product-section-price {
        &__discount {
          color: $second-color;
          font-size: 14px;
          text-decoration: line-through;
        }
        &__current {
          font-size: 20px;
          font-weight: 700;
          text-transform: uppercase;
          color: $main-black-color;
          padding: 3px 0 0 0;
        }
      }
      .shopping-cart {
        display: flex;
        .circled {
          width: 40px;
          height: 40px;
          border-radius: 50%;
          border: 1.3px solid #EAEBED;
          margin: 0px auto;
          display: flex;
          align-items: center;
          justify-content: center;
        }
      }
    }
    .buy-oneclick {
      padding: 10px;
      border: 0;
      background-color: $main-blue-color;
      color: #fff;
      border-radius: 5px;
      font-size: 12px;
      width: 100%;
    }
  }
  
}

sources.src\\product-page.scss:
.product-page {

  &__wrapper {
    display: flex;
  }
  .product-gallery {
    margin: 0 65px 0 0;
    &__main img {
      max-width: 570px;
    }
    &__row {
      max-width: 570px;
      display: flex;
      justify-content: flex-start;
      margin: 10px 0;
    }
  }
  .product-thumbnail {
    margin: 0 9px 0 0;
    img {
      border-radius: 5;
    }
    &.active {
      border: 2px solid #6F73EE;
      border-radius: 5px;
    }
  }

  // Product page info
  .product-info {
    &__container {
      max-width: 475px;
    }
  }
  .product-basic {
    &__title h1 {
      margin: 0 0 15px 0;
      font-weight: 600;
      font-size: 35px;
      line-height: 51px;      
      text-transform: uppercase;
    }
    .product-subtitle {
      margin: 0 0 25px 0;
      max-width: 380px;
      &__row {
        display: flex;
        justify-content: space-between;
        margin: 0 0 25px 0;
      }
      &__item {
        margin: 0 20px 0 0;
        font-style: normal;
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #5D6C7B;
        display: flex;
        align-items: center;
      }
      &__item img {
        margin: 0 10px 0 0;
      }
    }

    .product-price {
      display: flex;
      justify-content: space-between;
      margin-bottom: 35px;
    }
    .price-block {
      &__wrapper {
        display: flex;        
        border-radius: 5px;
        padding: 12px 15px;
        align-items: center;
      }
      &__main-price {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        text-decoration-line: line-through;
        color: #5D6C7B;
      }
      &__discount-price {
        font-style: normal;
        font-weight: 600;
        font-size: 25px;
        line-height: 36px;
        text-transform: uppercase;
      }
      &__icon {
        margin: 0 10px 0 0;
      }
      &__info {
        span {
          font-family: 'Jost';
          font-style: normal;
          font-weight: 400;
          font-size: 12px;
          line-height: 17px;          
          color: #5D6C7B;          
        }
        font-style: normal;
        font-weight: 700;
        font-size: 14px;
        line-height: 20px;
        color: #282739;
      }
    }
    .price-block-bg {
      background-color: $bg-color;
    }
  }


  .info-section {
    &__title {
      font-weight: 700;
      font-size: 16px;
      line-height: 23px;
      color: #282739;
      display: flex;
      align-items: center;
      margin-bottom: 25px;
      img {
        padding-left: 10px;
      }
    }
    &__row {
      display: flex;
      justify-content: space-between;
      margin: 0 0 20px 0;
    }
    &__row:nth-child(2) {
      margin-bottom: 0;
    }
    .product-info__border-line {
      border-bottom: 1px solid #EAEBED;
      margin: 30px 0;
    }
  }
  .info-subsection {
    &__label {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
    }
    .subsection-content {
      display: flex;
      justify-content: space-between;
      &__main-title {
        font-weight: 700;
        font-size: 20px;
        line-height: 24px;
        color: #282739;
      }
      &__secondary-title {
        font-weight: 500;
        font-size: 14px;
        line-height: 20px;
        color: #282739;

      }
    }
    .settings a {
      font-weight: 500;
      font-size: 14px;
      line-height: 20px;
      color: #6F73EE;
      text-decoration: none;
    }
  }
  .gift-section {
    margin: 0 0 20px 0;
    &__header {
      display: flex;
      justify-content: space-between;
      font-weight: 700;
      margin: 0 0 25px 0;
    }
    &__right-block {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
      span {
        color: #282739;
        font-size: 16px;
        font-weight: 700;
      }
    }
    &__body {
      background-color: $bg-color;
      border-radius: 10px;
      padding: 25px 20px;
    }
    .gift-box {
      &__title {
        font-weight: 700;
        font-size: 18px;
        margin: 0 0 25px 0;
      }
      &__row {
        display: flex;
        justify-content: space-between;
      }
    }
    .gift-block {
      display: flex;
      font-size: 12px;
      img {
        padding-right: 15px;
        width: 46px;
      }
    }
  }
  .product-color-section {
    margin: 0 0 30px 0;
  }
  .shopping {
    background-color: $bg-color;
    &__container {
      padding: 40px 30px;
    }
    &__header {
      display: flex;
      justify-content: space-between;
      padding: 0 0 25px 0;
      border-bottom: 1px solid #EAEBED;
    }
    &__title {
      font-size: 35px;
      font-weight: 700;
      color: #282739;
    }
    &__right-block {
      width: 40px;
      height: 40px;
      border-radius: 50%;
      border: 1.3px solid #EAEBED;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    &__middle {
      padding: 35px 0 0 0;
      display: flex;
    }

    .shopping-text {
      margin: 0 0 30px 0;
      &__title {
        font-weight: 700;
      }
      padding: 0 0 0 10px;
    }
    .shopping-cta {
      display: flex;
      justify-content: space-between;
      max-width: 414px;
    }
    .btn {
      padding: 20px 0;
      border-radius: 5px;
      text-align: center;
    }
    .btn-bordered {
      border: 1px solid $main-blue-color;
      background-color: #F4F7FB;
      color: $main-blue-color;
      font-weight: 500;
      width: 48%;
    }
    .btn-primary {
      width: 48%;
      background-color: $main-blue-color;
      color: #fff;
      font-weight: 500; 
    }
    &__footer {
      text-align: center;
      color: #6F73EE;
      font-size: 14px;
      font-weight: 500;
      margin-top: 25px;
    }
  }

  .product-box {
    display: flex;
    justify-content: center;
    align-items: center;
    
    width: 47%;
    padding: 30px 0;
    border: 1.5px solid #EAEBED;
    border-radius: 5px;
    text-align: center;

    &__label {
      font-weight: 700;
      margin-bottom: 15px;
    }
    &__sublabel {
      font-size: 14px;
      line-height: 20px;
      text-align: center;
      color: #5D6C7B;
      margin-top: 20px;
    }
    &.selected {
      border: 1.5px solid $main-blue-color;
    }
  }
}
