      font-weight: 600;
      margin-right: 20px;
    }
    &__price {
      padding: 10px 10px;
      margin-right: 20px;
    }
    &__params {
      padding: 10px 15px;
    }
    img {
     margin-left: 15px; 
    }
  }

  // Sidebar filter Section
  .category-filters {
    max-width: 255px;
    flex: 0 0 100%;
    background-color: $bg-color;
    margin: 0 40px 0 0;
    height: 590px;
    &__container {
      max-width: 212px;
      margin: 0 auto;
      padding: 20px 0;
    }
  }
  .filter-block {
    margin: 0 0 20px 0;
    &__header {
      margin-bottom: 20px;
      font-weight: 600;
    }
    &__item {
      margin-bottom: 10px;
      display: flex;
      align-items: center;
      color: $main-black-color;
      font-size: 14px;
      .filter {
        margin-right: 10px;
      }
    }
    &__item:last-child {
      margin: 0;
    }
    .filter-checkbox {
      position: relative;
      padding: 0 0 0 20px;
      line-height: 18px;
      
      input:checked + .filter-checkbox__hidden {
        position: absolute;
        top: 0;
        left: 0;
        width: 16px;
        height: 16px;
        background-color: #fff;
        border: 0;
        border-radius: 2px;
        background-image: url('./images/filters/checked.svg');
        background-repeat: no-repeat;
        background-position: center;
      }
      &__input {
        appearance: none;
        -moz-appearance: none;
        -webkit-appearance: none;
      }
      &__hidden {
        top: 0;
        left: 0;
        position: absolute;
        width: 16px;
        height: 16px;
        background-color: #fff;
        border: 1px solid #5D6C7B;
        border-radius: 2px;
      }
    }
  }
  .show-more-filters {
    font-size: 14px;
    color: $main-blue-color;
    text-align: center;
    img {
      padding: 0 5px;
    }
  }
  .price-range {
    margin: 0 0 17px 0;
  }
  .price-input {
    display: flex;
    justify-content: space-between;
    align-items: center;
    &__from {
      width: 68px;
      height: 35px;
      border: 1px solid #EAEBED;
      border-radius: 5px;
      padding-left: 10px;
      outline: none;
      border: 0;
    }
    &__to {
      width: 68px;
      height: 35px;
      border: 1px solid #EAEBED;
      border-radius: 5px;
      padding-left: 10px;
      outline: none;
      border: 0;
    }    
  }

