.product-page {
  .column-content {
    display: flex;
  }
  .product-gallery {
    margin: 0 65px 0 0;
    &__main img {
      max-width: 570px;
    }
    &__row {
      max-width: 570px;
      display: flex;
      justify-content: flex-start;
      margin: 10px 0;
    }
  }
  .product-thumbnail {
    margin: 0 9px 0 0;
    img {
      border-radius: 5;
    }
    &.active {
      border: 2px solid #6F73EE;
      border-radius: 5px;
    }
  }
  // Product page info
  .product-info {
    &__container {
      max-width: 475px;
    }
  }
  .product-basic {
    &__title h1 {
      padding: 0;
      margin: 0 0 15px 0;
      font-weight: 600;
      font-size: 35px; 
      text-transform: uppercase;
    }
    .product-subtitle {
      margin: 0 0 25px 0;
      max-width: 380px;
      &__row {
        display: flex;
        justify-content: space-between;
        margin: 0 0 25px 0;
      }
      &__row:last-child {
        margin-bottom: 32px;
      }
      &__item {
        margin: 0 20px 0 0;
        font-style: normal;
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #5D6C7B;
        display: flex;
        align-items: center;
      }
      &__item img {
        margin: 0 10px 0 0;
      }
    }

    .product-price {
      display: flex;
      justify-content: space-between;
      margin-bottom: 35px;
    }
    .price-block {
      &__wrapper {
        display: flex;        
        border-radius: 5px;
        padding: 12px 15px;
        align-items: center;
      }
      &__main-price {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        text-decoration-line: line-through;
        color: #5D6C7B;
      }
      &__discount-price {
        font-style: normal;
        font-weight: 600;
        font-size: 25px;
        line-height: 36px;
        text-transform: uppercase;
      }
      &__icon {
        margin: 0 10px 0 0;
      }
      &__info {
        font-style: normal;
        font-weight: 700;
        font-size: 14px;
        line-height: 20px;
        color: #282739;

        span {
          font-family: 'Jost';
          font-style: normal;
          font-weight: 400;
          font-size: 12px;
          line-height: 17px;          
          color: #5D6C7B;          
        }
      }
    }
    .price-block-bg {
      background-color: $bg-color;
    }
  }
  .info-section {
    &__title {
      font-weight: 700;
      font-size: 16px;
      line-height: 23px;
      color: #282739;
      display: flex;
      align-items: center;
      // margin-bottom: 25px;
      img {
        padding-left: 10px;
      }
    }
    &__row {
      display: flex;
      justify-content: space-between;
      // margin: 0 0 20px 0;
    }
    &__row:nth-child(2) {
      margin-bottom: 0;
    }
    .product-info__border-line {
      border-bottom: 1px solid #EAEBED;
      margin: 30px 0;
    }
    .h-checkbox:checked + .product-box {
      border: 2px solid #6F73EE;
    }
    
  }
  .info-subsection {
    &__label {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
      margin-bottom: 10px;
    }
    .subsection-content {
      display: flex;
      justify-content: space-between;
      &__main-title {
        font-weight: 700;
        font-size: 20px;
        line-height: 24px;
        color: #282739;        
      }
      &__secondary-title {
        font-weight: 500;
        font-size: 14px;
        line-height: 20px;
        color: #282739;
        margin-bottom: 10px;
      }
      &__sub {
        font-weight: 600;
      }
    }
    .settings a {
      font-weight: 500;
      font-size: 14px;
      line-height: 20px;
      color: #6F73EE;
      text-decoration: none;
    }
  }
  .gift-section {
    margin: 0 0 20px 0;
    &__header {
      display: flex;
      justify-content: space-between;
      font-weight: 700;
      margin: 0 0 25px 0;
    }
    &__right-block {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
      span {
        color: #282739;
        font-size: 16px;
        font-weight: 700;
      }
    }
    &__body {
      background-color: $bg-color;
      border-radius: 10px;
      padding: 25px 20px;
    }
    .gift-box {
      &__title {
        font-weight: 700;
        font-size: 18px;
        margin: 0 0 25px 0;
      }
      &__row {
        display: flex;
        justify-content: space-between;
      }
    }
    .gift-block {
      display: flex;
      font-size: 12px;
      img {
        padding-right: 15px;
        width: 46px;
      }
    }
  }
  .product-color {
    margin: 0 0 30px 0;
  }
  .shopping {
    background-color: $bg-color;
    &__container {
      padding: 40px 30px;
    }
    &__header {
      display: flex;
      justify-content: space-between;
      padding: 0 0 25px 0;
      border-bottom: 1px solid #EAEBED;
    }
    &__title {
      font-size: 35px;
      font-weight: 700;
      color: #282739;
    }
    &__right-block {
      width: 40px;
      height: 40px;
      border-radius: 50%;
      border: 1.3px solid #EAEBED;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    &__middle {
      padding: 35px 0 0 0;
      display: flex;
    }

    .shopping-text {
      margin: 0 0 30px 0;
      &__title {
        font-weight: 700;
      }
      padding: 0 0 0 10px;
    }
    .shopping-cta {
      display: flex;
      justify-content: space-between;
      max-width: 414px;
    }
    .btn {
      padding: 17px 0;
      border-radius: 5px;
      text-align: center;
    }
    .btn-bordered {
      border: 1px solid $main-blue-color;
      background-color: #F4F7FB;
      color: $main-blue-color;
      font-weight: 500;
      width: 48%;
    }
    .btn-primary {
      width: 48%;
      background-color: $main-blue-color;
      color: #fff;
      font-weight: 500; 
    }
    &__footer {
      text-align: center;
      color: #6F73EE;
      font-size: 14px;
      font-weight: 500;
      margin-top: 25px;
    }
  }

  .product-box-h {
    height: 80px;
  }
  .empty-service-box {
    min-height: 55px;
  }
  .empty-color-box {
    min-height: 85px;
  }
  .product-box {
    display: flex;
    justify-content: center;
    align-items: center;
    
    width: 227px;
    padding: 30px 0;
    border: 1.5px solid #EAEBED;
    border-radius: 10px;
    text-align: center;

    &__label {
      font-weight: 700;
      margin-bottom: 15px;
    }
    &__sublabel {
      font-size: 14px;
      line-height: 20px;
      text-align: center;
      color: #5D6C7B;
      margin-top: 15px;
    }
    &.selected {
      // border: 1.5px solid $main-blue-color;
    }
  }
}

