sources.src\\general.scss:
body {
  font-family: 'Jost', sans-serif;
}
.container {
  max-width: 1110px;
  margin: 0 auto;
}
a {
  text-decoration: none;
}
ol.breadscrumb__list {
  display: flex;
  list-style: none;
  padding: 0;
  li {
    margin: 0 10px 0 0;
    color: #5D6C7B;
  }
  li.active {
    color: #282739;
  }
  li:first-child:before {
    content: '';
    width: 15px;
    height: 15px;
    background-image: url('./images/home.svg');
    background-repeat: no-repeat;
    margin: 0 10px 0 0;
    padding: 0 0 0 15px;
  }
  li:before {
    content: '/';
    padding: 0 10px 0 0;
  }
}

// Checkbox controls

.h-checkbox {
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;
}

sources.src\\header.scss:
.header {
  border-bottom: 2px solid #ECF3FF;
  margin: 0 0 25px 0;
  &__top {
    border: 1px solid #ECF3FF;
    .wrapper {
      height: 40px;
      display: flex;
      justify-content: space-between;
      align-items: center;  
    }
  }
  .header-top-menu {
    display: flex;
    &__block {
      margin: 0 30px 0 0;
    }
    &__link {
      color: #5D6C7B;
      text-decoration: none;
      font-size: 12px;
      line-height: 17px;        
    }
  }
  .block-social img{
    margin: 0 8px 0 0;
  }
  .header-contact {
    display: flex;
    flex: 0 0 150px;
    position: relative;
    z-index: 9;
    &__call {
      display: flex;
      align-items: center;
    }
    &__phone {
      font-size: 12px;
      line-height: 22px;
      font-weight: bold;
      color: #282739;
    }
    &__phone-icon {
      margin: 0 0 0 8px;
    }
  }

  &__middle {
    margin: 20px 0 35px 0;
    .wrapper {
      display: flex;
      align-items: center;
      justify-content: space-between;
    }
    .header-logo {
      width: 165px;
    }
    .header-catalog {
      margin: 0 20px 0 0;
    }
    .menu-catalog__link {
      padding: 12px 15px;
      display: flex;
      justify-content: center;
      align-items: center;
      background-color: $main-blue-color;
      color: #fff;
      border-radius: 5px;
      font-size: 16px;
      font-weight: 600;
    }
    .menu-catalog__link.show{
      background: #8185F9; 
      .menu-catalog__icon {
        background-image: url('./images/header/catalog-icon-open.svg');
      }
    }
    .menu-catalog__icon {
      background-image: url('./images/header/catalog-icon.svg');
      background-repeat: no-repeat;
      width: 13px;
      height: 13px;
      margin: 0 15px 0 0;
    }

    .header-search {
      height: 40px;
      border: 2px solid $main-blue-color;
      border-radius: 5px;
    }
    .header-search {
      max-width: 605px;
      .wrapper {
        display: flex;
        align-items: center;
      }
    }
    .form-search {
      display: flex;
      width: 100%;
    }
    .search-dropdown {
      width: 76px;
      height: 36px;
      display: flex;
      margin: 2px 0 0 2px;
      justify-content: center;
      align-items: center;
      border-radius: 5px;
      background-color: $bg-color;
      &__label {
        margin: 0 10px 0 0;
        font-size: 14px;
        color: #282739;
      }
      &__icon {
        background-image: url('./images/header/arrow-down.svg');
        background-repeat: no-repeat;
        width: 8px;
        height: 7px;            
      }
    }
    .searchbar {
      position: relative;
      max-width: 100%;
      
      &__input {
        width: 485px;
        height: 36px;
        border: 0;
        outline: none;
        padding-left: 20px;
      }
      &__input::placeholder {
        font-size: 14px;
        color: #5D6C7B;
      }
      &__submit {
        position: absolute;
        top: -1px;
        right: -2px;
        width: 42px;
        height: 42px;
        border: 0;
        border-top-right-radius: 5px;
        border-bottom-right-radius: 5px;
        background-color: $main-blue-color;
        background-image: url('./images/header/search-icon.svg');
        background-repeat: no-repeat;
        background-position: center;
      }
    }
    .header-shopping {
      width: 175px;
      display: flex;
      margin: 0 0 0 47px;
      align-items: baseline;
      position: relative;
      z-index: 0;
      &__block {
        margin: 0 5px 0 0;
      }
      &__block img {
        padding: 0 10px 0 0;
      }
      .h-shopping-cart {
        display: flex;
        align-items: center;
        cursor: pointer;
      }
/*       .cart {
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 14px;
        line-height: 26px;
      } */
    }
  }
  
  /* Overlay category */
  .header-overlay {
    position: relative;
    z-index: 1;
    opacity: 0;
    visibility: hidden;
    &__wrapper {
      z-index: 2;
      background-color: #fff;
      display: flex;
      position: absolute;
      top: -35px;
      width: 890px;
      min-height: 270px;
      background: #FFFFFF;
      box-shadow: 0px 10px 30px rgba(111, 115, 238, 0.1);
      border-bottom-left-radius: 5px;
      border-bottom-right-radius: 5px;
    }
    .catalog-category {
      margin-right: 30px;
      &__list {
        background-color: $bg-color;
        list-style: none;
        padding: 20px 0 20px 20px;
        margin: 10px 10px 0 10px;
        border-radius: 5px;
        width: 235px;
      }
      &__item {
        margin-bottom: 20px;
      }
      &__item:last-child {
        margin-bottom: 0;
      }
      &__icon {
        margin-right: 10px;
      }
      &__link {
        display: flex;
        align-items: center;
        font-weight: 600;
        font-size: 16px;
        line-height: 23px;
        color: #282739;
        p {
          margin: 0;
          padding: 0;
        }
        &.active {
          font-weight: 600;
          font-size: 16px;
          line-height: 23px;
          color: #6F73EE;
        }
        &:hover {
          color: #6F73EE;
        }
      }
    }
    .catalog-subcategory {
      display: flex;
      &__block {        
        margin-right: 70px;
      }
      &__block:last-child {
        margin-right: 0;
      }
      &__title {
        font-weight: 600;
        font-size: 16px;
        line-height: 23px;
        color: #282739;
      }
      &__list {
        list-style: none;
        padding: 0;
        margin: 0;
      }
      &__item {
        margin-bottom: 10px;
      }
      &__link {
        font-weight: 400;
        font-size: 14px;
        line-height: 20px;
        color: #5D6C7B;
      }
    }
    &.show {
      opacity: 1;
      visibility: visible;
    }
  }


  // Shopping cart checkout
  .s-cart-popup {
    max-width: 305px;
    margin-left: 100px;
    position: absolute;
    z-index: 10;
    right: 10px;
    top: 34px;
    opacity: 0;
    visibility: hidden;
    box-shadow: 0px -4px 50px rgba(0, 0, 0, 0.1);
    &__container {
      width: 265px;
      margin: 0 auto;
      display: flex;
      align-items: center;
      justify-content: space-between;
    }
    &__top {
      background-color: #F4F7FB;
      height: 56px;
      border-radius: 5px 5px 0 0;
    }
    &__content {
      background-color: #fff;
    }
    &__title {
      font-weight: 500;
      font-size: 16px;
      line-height: 16px;
      color: #282739;
    }
    &__info {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
    }
    &__content {
      overflow-y: scroll;
      max-height: 210px;
    }
    &__item {
      display: flex;
      align-items: center;
      border-bottom: 1px solid #EAEBED;
      padding: 15px 15px;
      &:last-child {
        border: 0;
      }
    }
    &__image {
      margin-right: 0;
    }
    &__details {
      width: 55%;
    }
    &__remove {
      width: 10%;
      cursor: pointer;
    }
    .s-cart-details {      
      &__title {
        font-weight: 500;
        font-size: 14px;
        line-height: 20px;
        color: #282739;
        margin: 0;        
      }
      &__price {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #282739;        
      }
      &__qty {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #282739;
      }
    }
    &__footer {
      background: #FFFFFF;
      box-shadow: 0px -4px 50px rgba(0, 0, 0, 0.1);
      border-radius: 0px 0px 5px 5px;
      
    }
    .s-cart-footer {
      padding: 20px 0;
      &__label {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #5D6C7B;
      }
      &__price {
        font-weight: 600;
        font-size: 18px;
        line-height: 26px;        
        color: #282739;
        margin: 0;
      }
      &__checkout {
        background-color: #6F73EE;
        border-radius: 5px;
        color: #fff;
        padding: 12px 29px;
        font-size: 12px;
      }
    }
    &.show {
      opacity: 1;
      visibility: visible;
    }
  }

  /* Call center popup */
  .callcenter-popup {
    max-width: 305px;
    margin-left: 100px;
    position: absolute;
    z-index: 10;
    right: 10px;
    top: 34px;
    right: 22px;
    background-color: #fff;
    opacity: 0;
    visibility: hidden;
    border-radius: 5px;
    box-shadow: 0px -4px 50px rgba(0, 0, 0, 0.1);
    &__container {
      width: 265px;
      margin: 0 auto;
      display: flex;
      align-items: center;
      justify-content: space-between;
    }
    &__item {
      display: flex;
      align-items: center;
      border-bottom: 1px solid #EAEBED;
      padding: 20px 0 20px 20px;      
      &:last-child {
        border: 0;
      }
    }
    .сс-popup-details {
      &__title {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #5D6C7B;
        margin: 0 0 2px 0;
      }
      &__price {
        font-style: normal;
        font-weight: 600;
        font-size: 16px;
        line-height: 23px;
        color: #282739;        
        margin: 0 0 2px 0;
      }
      &__tel {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #282739;        
      }
    }
    &.show {
      opacity: 1;
      visibility: visible;
    }
  }


  &__menu {
    height: 40px;
    background-color: $bg-color;
  }
  .header-menu {
    &__menu {
      display: flex;
      align-items: center;
      height: 40px;
    }
  }
  .menu-list {
    list-style: none;
    display: flex;
    padding: 0;
    li {
      margin: 0 30px 0 0;
    }
    &__link {
      text-decoration: none;
      font-weight: 500;
      font-size: 14px;
      font-size: normal;
      line-height: 20px;
      color: #282739;
    }
    &__menu-icon {
      background-color: $main-blue-color;
      color: #fff;
      font-size: 10px;
      padding: 5px 7px;
      border-radius: 300px;
      &.bank {
        margin: 0 0 0 8px;
      }
      &.percent {
        background-image: url('./images/header/percentage.svg');
        background-repeat: no-repeat;
        background-position: center;
        margin: 0 0 0 8px;
        padding: 5px 10px;
      }
    }
  }
}

sources.src\\products.scss:
.sort-btn {
  border: 1.3px solid $main-blue-color;
  color: $main-blue-color;
  border-radius: 5px;
  display: flex;
  font-size: 12px;
  align-items: center;
}
.product {
  // margin-bottom: 600px;
  &__breadcrumb {
    margin: 0 0 30px 0;
  }
  &__page-content {
    display: flex;
    margin: 0 0 100px 0;
  }
  /* Sort by Section */
  &__sortby-header {
    display: flex;
    justify-content: space-between;
    margin: 0 0 40px 0;
    .label {
      font-size: 25px;
      color: $main-black-color;
      text-transform: uppercase;
      font-weight: 700;
    }
  }
  .sortby-controls {
    display: flex;
    align-items: center;
    &__label {
      color: $main-black-color;
      font-size: 14px;
      font-weight: 600;
      margin-right: 20px;
    }
    &__price {
      padding: 10px 10px;
      margin-right: 20px;
    }
    &__params {
      padding: 10px 15px;
    }
    img {
     margin-left: 15px; 
    }
  }
  .sortby-price {
    z-index: 99;
    position: relative;
    cursor: pointer;

    &.selected {
      background-color: #6F73EE;
      color: #fff;
    }
  }
  .sortby-price-popup {
   position: absolute;
   background-color: $bg-color;
   top: 47px;
   left: 0px;
   padding: 10px 10px;
   width: 144px;
   height: 47px;
   border-radius: 5px;
   visibility: hidden;
   opacity: 0;
   z-index: 999;

   &__item {
    font-weight: 400;
    font-size: 14px;
    line-height: 20px;
    color: #282739;
    margin-bottom: 7px;
    &.selected {
      color: #6F73EE;
     }
   }
   &.show {
    visibility: visible;
    opacity: 1; 
   }
  }
  .sortby-params-popup {
    position: absolute;
    background-color: $bg-color;
    top: 47px;
    left: 0px;
    padding: 10px 10px;
    width: 144px;
    height: 47px;
    border-radius: 5px;
    visibility: hidden;
    opacity: 0;
    z-index: 999;
 
    &__item {
     font-weight: 400;
     font-size: 14px;
     line-height: 20px;
     color: #282739;
     margin-bottom: 7px;
     &.selected {
       color: #6F73EE;
     }
    }
    &.show {
     visibility: visible;
     opacity: 1; 
    }
  }
  .sortby-params {
    z-index: 99;
    position: relative;
    cursor: pointer;

    &.selected {
      background-color: #6F73EE;
      color: #fff;
    }
  }

  // Sidebar filter Section
  .page-sidebar {
    max-width: 255px;
    flex: 0 0 100%;
    background-color: $bg-color;
    margin: 0 40px 220px 0;
    height: 590px;
    &__container {
      max-width: 212px;
      margin: 0 auto;
      padding: 20px 0;
    }
  }
  .filter-block {
    margin: 0 0 20px 0;
    &__header {
      margin-bottom: 20px;
      font-weight: 600;
    }
    &__item {
      margin-bottom: 10px;
      display: flex;
      align-items: center;
      color: $main-black-color;
      font-size: 14px;
      .filter {
        margin-right: 10px;
      }
    }
    &__item:last-child {
      margin: 0;
    }
    .filter-checkbox {
      position: relative;
      padding: 0 0 0 20px;
      line-height: 18px;
      
      input:checked + .filter-checkbox__hidden {
        position: absolute;
        top: 0;
        left: 0;
        width: 16px;
        height: 16px;
        background-color: #fff;
        border: 0;
        border-radius: 2px;
        background-image: url('./images/filters/checked.svg');
        background-repeat: no-repeat;
        background-position: center;
      }
      &__input {
        appearance: none;
        -moz-appearance: none;
        -webkit-appearance: none;
      }
      &__hidden {
        top: 0;
        left: 0;
        position: absolute;
        width: 16px;
        height: 16px;
        background-color: #fff;
        border: 1px solid #5D6C7B;
        border-radius: 2px;
      }
    }
  }
  .show-more-filters {
    font-size: 14px;
    color: $main-blue-color;
    text-align: center;
    img {
      padding: 0 5px;
    }
  }
  .price-range {
    margin: 0 0 17px 0;
  }
  .price-input {
    display: flex;
    justify-content: space-between;
    align-items: center;
    &__from {
      width: 68px;
      height: 35px;
      border: 1px solid #EAEBED;
      border-radius: 5px;
      padding-left: 10px;
      outline: none;
      border: 0;
    }
    &__to {
      width: 68px;
      height: 35px;
      border: 1px solid #EAEBED;
      border-radius: 5px;
      padding-left: 10px;
      outline: none;
      border: 0;
    }    
  }

  /* Sidebar adv section */

  .sidebar-adv {
    background: radial-gradient(102.16% 238.93% at -2.16% 100%, #FFCF71 0%, #F3A712 100%) /* warning: gradient uses a rotation that is not supported by CSS and may not behave as expected */;
    border-radius: 10px;
    margin-bottom: 20px;
    .product-of-month {
      &__container {
        display: flex;
        align-items: center;
      }
      &__header {
        height: 60px;
        border-bottom: 1px solid rgb(234, 234, 234);
        // border: 1px solid red;
        margin-top: -25px;
        margin-bottom: 15px;
      }
      &__icon {
        margin-right: 12px;
      }
      &__title {
        font-weight: 500;
        font-size: 18px;
        line-height: 26px;
        color: #FFFFFF;
      }
      &__image {
        background-color: #F0F1F5;
        width: 61px;
        height: 61px;
        border-radius: 5px;
        margin-right: 20px;
        img {
          margin: 10px 0 0 0;
        }
      }
      &__label {
        font-weight: 400;
        font-size: 16px;
        line-height: 23px;
        color: #FFFFFF;
        padding: 0;
        margin: 0;
      }
      &__price {
        font-weight: 500;
        font-size: 20px;
        line-height: 24px;
        color: #FFFFFF;
      }
    }
  }

  .sidebar-question {
    background: radial-gradient(71.16% 199.4% at 82.43% 76.38%, #8FE865 0%, #49AC1B 100%) /* warning: gradient uses a rotation that is not supported by CSS and may not behave as expected */;
    border-radius: 10px;
    height: 90px;

    &__container {
      display: flex;
      align-items: center;
      max-width: 212px;
      margin: 0 auto;
    }
    &__image {
      margin-left: -15px;
    }
    &__body {
      font-weight: 500;
      font-size: 16px;
      line-height: 18px;
      color: #FFFFFF;      
    }
  }
  
  /* Product Body section */
  .products-body {
    max-width: 825px;
    flex: 0 0 825px;
  }
  .products-list {
    display: flex;
    justify-content: flex-start;
    margin-bottom: 30px;
  }
  .product-list-item {
    border: 1.5px solid #EAEBED;
    border-radius: 10px;
    max-width: 252px;
    margin: 0 20px 0 0;
    flex: 0 0 100%;
  }
  .product-thumbnail {
    background-color: #F0F1F5;
    position: relative;
    display: block;
    overflow: hidden;
    height: 228px;
    &__container {
      padding: 10px;
    }
  }
  .image-switch {
    position: absolute;
    left: 0;
    top: -10px;
    z-index: 20;
    width: 100%;
    height: 100%;
    display: flex;

    &__item {
      flex-grow: 1;
      cursor: pointer;
    }
    &__image {
      position: absolute;
      left: 50%;
      top: 25px;
      z-index: 2;
      width: 100%;
      height: 90%;
      transform: translateX(-50%);
      pointer-events: none;
      background-color: #F0F1F5;
      display: flex;
      align-items: center;
      justify-content: center;
      opacity: 0;
      img {
        display: block;
        max-width: 100;
        height: 100%;
        object-fit: cover;
      }
    }
    &__item:first-child .image-switch__image {
      opacity: 1;
      z-index: -1;
    }
/*     &__item:hover .image-switch__image {
      opacity: 1;
      z-index: -1;
    } */
  }
  .thumbnail-navs {
    display: flex;
    padding: 0;
    margin: 0;
    position: absolute;
    list-style: none;
    z-index: 999;
    li {
      position: relative;
      border-radius: 100%;
      width: 40px;
      height: 40px;
      background-color: #fff;
      display: flex;
      align-items: center;
      justify-content: center;
      img {
        position: absolute;
      }
    }
    .arrow-left {
      top: 50px;
      left: 0;
    }
    .arrow-right {
      top: 50px;
      left: 150px;
    }
  }
  .adv-block {
    background-color: $bg-color;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    h4 {
      font-weight: 600;
      font-size: 18px;
      line-height: 22px;
      color: #282739;
      padding: 0;
      margin: 0;
    }
    p {
      font-weight: 400;
      font-size: 14px;
      line-height: 18px;
      color: #282739;
    }
    &__cta {
      font-weight: 500;
      font-size: 14px;
      line-height: 20px;
      color: #6F73EE;
      display: flex;
      align-items: center;
      img {
        padding: 0 5px;
      }
    }
    .adv-block-body {
      img {
        margin-top: -50px;
        margin-bottom: -25px;
        width: 207px;
      }
    }

    &__container {
      padding: 35px 30px;
    }


  }
  .product-marketing {
    display: flex;
    justify-content: space-between;
    position: relative;
    z-index: 99;

    &__label {
      font-size: 12px;
      padding: 6px 10px;
      border-radius: 5px;
      height: 15px;
      line-height: 16px;
    }
    &__label.hit {
      background-color: #EE685F;
      text-transform: uppercase;
      color: #fff;
    }
    &__label.new {
      background-color: #75D14A;
      color: #fff;
    }
    &__compare {
      background-color: #F3F4F7;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: 50px;
      width: 35px;
      height: 35px;
    }
    &__compare img {
      width: 20px;
      height: 12px;
    
    }
  }
  .product-body {
    &__container {
      padding: 20px 25px;
    }
    .product-title {
      font-weight: 700;
      margin: 0 0 20px 0;
      // font-weight: 600;
      font-size: 18px;
      line-height: 26px;
      color: #282739;
    }
    .params-list {
      display: flex;
      justify-content: space-between;
      margin-bottom: 10px;
      align-items: center;
      line-height: 28px;
      &__item {
        color: #5D6C7B;
        font-size: 14px;
        display: flex;  
      }
      &__item img {
        margin-right: 5px;
      }
    }
    .product-shopping {
      display: flex;
      justify-content: space-between;
      margin: 0 0 20px 0;
      max-width: 206px;
      .product-section-price {
        &__discount {
          color: $second-color;
          font-size: 14px;
          text-decoration: line-through;
        }
        &__current {
          font-size: 20px;
          font-weight: 700;
          text-transform: uppercase;
          color: $main-black-color;
          padding: 3px 0 0 0;
        }
      }
      .shopping-cart {
        display: flex;
        .circled:last-child {
          margin-left: 10px;
        }
        .circled {
          width: 40px;
          height: 40px;
          border-radius: 50%;
          border: 1.3px solid #EAEBED;
          margin: 0px auto;
          display: flex;
          align-items: center;
          justify-content: center;
        }
      }
    }
    .buy-oneclick {
      border: 0;
      background-color: $main-blue-color;
      color: #fff;
      border-radius: 5px;
      font-size: 12px;
      width: 100%;
      height: 37px;
    }
  }

    // Subscription section

    &__subscription {
      background-color: $main-blue-color;
      height: 100px;   
      // margin-bottom: 50px; 
    }
    .product-subscription {

      .subscription-container {
        display: flex;
        align-items: center;
        justify-content: space-between;
      }
      
      &__column {
        padding: 25px 0;
      }
      

      .subscription-label {
        width: 425px;
        font-weight: 600;
        font-size: 18px;
        line-height: 26px;
        text-transform: uppercase;
        color: #FFFFFF;
      }
      .subscription-form__form {
        display: flex;
        justify-content: space-between;
        width: 570px;
      }
      .subscription-form {
        display: flex;
        justify-content: space-between;
        &__input {
          background: rgba(255, 255, 255, 0.2);
          border-radius: 5px;
          border: 0;
          width: 395px;
          height: 53px;
          color: #fff;
          font-size: 16px;
          outline: none;
          padding-left: 15px;
          &::placeholder {
            color: #fff;
          } 
        }
        &__submit {
          background-color: #fff;
          border: 0;
          outline: none;
          width: 140px;
          height: 53px;
          border-radius: 5px;
          text-align: center;
          color: #6F73EE;
          font-size: 16px;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
}

.footer {
  padding: 55px 0 0 0;
  background-color: #F4F7FB;
  .footer-content {
    display: flex;
    justify-content: space-between;
    margin-bottom: 50px;
    &__header {
      display: flex;
      justify-content: space-between;
    }
    &__column:first-child {
      display: flex;
      justify-content: space-between;
      width: 510px;
    }
    &__column:last-child {
      width: 510px;
    }
    &__for-buyer {
      display: flex;
    }
    .buyer-column-1 {
      margin: 0 45px 0 0;
    }
    .nav-row-1 {
      display: flex;
      .footer-nav {
        margin-right: 50px;
      }
    }
    .nav-row-2 {
      display: flex;
      justify-content: space-between;
    }
  }
  .w-header {
    min-height: 25px;
  }
  .footer-nav {
    &__row {
      margin-bottom: 20px;
    }
    &__title {
      font-weight: 500;
      font-size: 18px;
      line-height: 26px;      
      color: #282739;
      margin-bottom: 25px;
    }
    &__cta {
      font-weight: 500;
      font-size: 12px;
      line-height: 17px;
      color: #6F73EE;
    }
    &__list {
      padding: 0;
      list-style: none;
      li {
        margin: 0 0 10px 0;
      }
    }
    &__link {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
    }
    &__contact-phone_phone {
      font-weight: 500;
      font-size: 16px;
      line-height: 23px;
      color: #282739;
    }
    &__contact {
      &.label {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #5D6C7B;
      }
      &.number {
        font-weight: 500;
        font-size: 16px;
        line-height: 23px;        
        color: #282739;
      }
      &.time {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #5D6C7B;
      }
    }
  }
  .footer-contact > div {
    margin-bottom: 5px;
  }

  .footer-social {
    display: flex;
    align-items: center;
    justify-content: space-between;
    &__column {
      display: flex;
      align-items: center;
    }
    &__logo {
      margin-right: 50px;
    }
    &__app-markets {
      display: flex;
      align-items: center;
      img:first-child {
        margin-right: 10px;
      }
    }
  }
  .f-social-block {
    display: flex;
    &__label {
      font-weight: 500;
      font-size: 14px;
      line-height: 20px;
      color: #282739;
    }
    &__info {
      font-weight: 400;
      font-size: 10px;
      line-height: 14px;
      color: #5D6C7B;
    }
    &__item {
      display: flex;
      background-color: #fff;
      padding: 7px 12px 7px 12px;
      border-radius: 5px;
      margin-right: 10px;
      img {
        margin-right: 10px;
      }
      &:last-child {
        margin-right: 0;
      }
    }
  }

  
  .footer-copyright {
    display: flex;
    justify-content: space-between;

    &__container {
      display: flex;
      justify-content: space-between;
    }
    .footer-copyright-block {
      width: 520px;
    }
    &__list {
      display: flex;
      list-style: none;
      margin: 0;
      padding: 0;
      li:first-child {
        margin-right: 45px;
      }
    }
    &__link {
      font-weight: 400;
      font-size: 12px;
      line-height: 17px;
      color: #5D6C7B;
    }
    .payment-block {
      display: flex;
      
      &__container {
        background-color: #fff;
        border-radius: 3px;
        border: 1px solid #EAEBED;
        padding: 1px 5px;
        margin-right: 5px;
      }
      img {
        border-radius: 3px;  
        width: 24px;
        height: 10px;      
        &:last-child {
          margin-right: 0;
        }
      }
    }
    .chat-block {
      width: 157px;
      display: flex;
      align-items: center;
      justify-content: space-between;
    }
  }

  .bottom-h-line {
    border-bottom: 1.4px solid #dcdcdc;
    width: 1110px;
    margin: 30px 0;
  }
}

sources.src\\product-page.scss:
.product-page {
  .column-content {
    display: flex;
  }
  .product-gallery {
    margin: 0 65px 0 0;
    &__main img {
      max-width: 570px;
    }
    &__row {
      max-width: 570px;
      display: flex;
      justify-content: flex-start;
      margin: 10px 0;
    }
  }
  .product-thumbnail {
    margin: 0 9px 0 0;
    img {
      border-radius: 5;
    }
    &.active {
      border: 2px solid #6F73EE;
      border-radius: 5px;
    }
  }
  // Product page info
  .product-info {
    &__container {
      max-width: 475px;
    }
  }
  .product-basic {
    &__title h1 {
      padding: 0;
      margin: 0 0 15px 0;
      font-weight: 600;
      font-size: 35px; 
      text-transform: uppercase;
    }
    .product-subtitle {
      margin: 0 0 25px 0;
      max-width: 380px;
      &__row {
        display: flex;
        justify-content: space-between;
        margin: 0 0 25px 0;
      }
      &__row:last-child {
        margin-bottom: 32px;
      }
      &__item {
        margin: 0 20px 0 0;
        font-style: normal;
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        color: #5D6C7B;
        display: flex;
        align-items: center;
      }
      &__item img {
        margin: 0 10px 0 0;
      }
    }

    .product-price {
      display: flex;
      justify-content: space-between;
      margin-bottom: 35px;
    }
    .price-block {
      &__wrapper {
        display: flex;        
        border-radius: 5px;
        padding: 12px 15px;
        align-items: center;
      }
      &__main-price {
        font-weight: 400;
        font-size: 12px;
        line-height: 17px;
        text-decoration-line: line-through;
        color: #5D6C7B;
      }
      &__discount-price {
        font-style: normal;
        font-weight: 600;
        font-size: 25px;
        line-height: 36px;
        text-transform: uppercase;
      }
      &__icon {
        margin: 0 10px 0 0;
      }
      &__info {
        font-style: normal;
        font-weight: 700;
        font-size: 14px;
        line-height: 20px;
        color: #282739;

        span {
          font-family: 'Jost';
          font-style: normal;
          font-weight: 400;
          font-size: 12px;
          line-height: 17px;          
          color: #5D6C7B;          
        }
      }
    }
    .price-block-bg {
      background-color: $bg-color;
    }
  }
  .info-section {
    &__title {
      font-weight: 700;
      font-size: 16px;
      line-height: 23px;
      color: #282739;
      display: flex;
      align-items: center;
      // margin-bottom: 25px;
      img {
        padding-left: 10px;
      }
    }
    &__row {
      display: flex;
      justify-content: space-between;
      // margin: 0 0 20px 0;
    }
    &__row:nth-child(2) {
      margin-bottom: 0;
    }
    .product-info__border-line {
      border-bottom: 1px solid #EAEBED;
      margin: 30px 0;
    }
    .h-checkbox:checked + .product-box {
      border: 2px solid #6F73EE;
    }
    
  }
  .info-subsection {
    &__label {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
      margin-bottom: 10px;
    }
    .subsection-content {
      display: flex;
      justify-content: space-between;
      &__main-title {
        font-weight: 700;
        font-size: 20px;
        line-height: 24px;
        color: #282739;        
      }
      &__secondary-title {
        font-weight: 500;
        font-size: 14px;
        line-height: 20px;
        color: #282739;
        margin-bottom: 10px;
      }
      &__sub {
        font-weight: 600;
      }
    }
    .settings a {
      font-weight: 500;
      font-size: 14px;
      line-height: 20px;
      color: #6F73EE;
      text-decoration: none;
    }
  }
  .gift-section {
    margin: 0 0 20px 0;
    &__header {
      display: flex;
      justify-content: space-between;
      font-weight: 700;
      margin: 0 0 25px 0;
    }
    &__right-block {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #5D6C7B;
      span {
        color: #282739;
        font-size: 16px;
        font-weight: 700;
      }
    }
    &__body {
      background-color: $bg-color;
      border-radius: 10px;
      padding: 25px 20px;
    }
    .gift-box {
      &__title {
        font-weight: 700;
        font-size: 18px;
        margin: 0 0 25px 0;
      }
      &__row {
        display: flex;
        justify-content: space-between;
      }
    }
    .gift-block {
      display: flex;
      font-size: 12px;
      img {
        padding-right: 15px;
        width: 46px;
      }
    }
  }
  .product-color {
    margin: 0 0 30px 0;
  }
  .shopping {
    background-color: $bg-color;
    &__container {
      padding: 40px 30px;
    }
    &__header {
      display: flex;
      justify-content: space-between;
      padding: 0 0 25px 0;
      border-bottom: 1px solid #EAEBED;
    }
    &__title {
      font-size: 35px;
      font-weight: 700;
      color: #282739;
    }
    &__right-block {
      width: 40px;
      height: 40px;
      border-radius: 50%;
      border: 1.3px solid #EAEBED;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    &__middle {
      padding: 35px 0 0 0;
      display: flex;
    }

    .shopping-text {
      margin: 0 0 30px 0;
      &__title {
        font-weight: 700;
      }
      padding: 0 0 0 10px;
    }
    .shopping-cta {
      display: flex;
      justify-content: space-between;
      max-width: 414px;
    }
    .btn {
      padding: 17px 0;
      border-radius: 5px;
      text-align: center;
    }
    .btn-bordered {
      border: 1px solid $main-blue-color;
      background-color: #F4F7FB;
      color: $main-blue-color;
      font-weight: 500;
      width: 48%;
    }
    .btn-primary {
      width: 48%;
      background-color: $main-blue-color;
      color: #fff;
      font-weight: 500; 
    }
    &__footer {
      text-align: center;
      color: #6F73EE;
      font-size: 14px;
      font-weight: 500;
      margin-top: 25px;
    }
  }

  .product-box-h {
    height: 80px;
  }
  .empty-service-box {
    min-height: 55px;
  }
  .empty-color-box {
    min-height: 85px;
  }
  .product-box {
    display: flex;
    justify-content: center;
    align-items: center;
    
    width: 227px;
    padding: 30px 0;
    border: 1.5px solid #EAEBED;
    border-radius: 10px;
    text-align: center;

    &__label {
      font-weight: 700;
      margin-bottom: 15px;
    }
    &__sublabel {
      font-size: 14px;
      line-height: 20px;
      text-align: center;
      color: #5D6C7B;
      margin-top: 15px;
    }
    &.selected {
      // border: 1.5px solid $main-blue-color;
    }
  }
}

sources.src\\shopping-cart.scss:
.shopping-cart {
	&__header {
		margin: 65px 0 50px 0;
	}
	&__title {
		font-weight: 600;
		font-size: 35px;
		line-height: 51px;		
		text-transform: uppercase;
		color: #282739;
	}

	&__body {
		background-color: $bg-color;
		border-radius: 10px;
	}
	&__container {
		display: flex;
		justify-content: center;
	}
	&__column {
		display: flex;
		flex-direction: column;
		align-items: center;
		margin: 47px 0;
		height: 230px;
		justify-content: space-between;
	}
	.shopping-cart-body {
		&__label {
			font-weight: 600;
			font-size: 25px;
			line-height: 36px;
			text-transform: uppercase;
			color: #282739;
		}
		&__sublabel {
			font-weight: 400;
			font-size: 14px;
			line-height: 20px;			
			color: #282739;
		}
		&__btn-catalog {
			display: block;
			text-decoration: none;
			color: #fff;			
			background: #6F73EE;			
			border: 1.3px solid #6F73EE;
			border-radius: 5px;
			padding: 13px 20px;
		}
	}
